    (283, 'incl_clientFnCallDefs', NULL, NULL, 0, 'none', '<table class="fieldListTable" style="border-collapse: collapse; width: 90%;" border="1" align="left">\r\n<thead>\r\n<tr>\r\n<th scope="col">Client-side Action</th>\r\n<th scope="col">Examples</th>\r\n<th scope="col">Targeted Code File</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td>SETNGXMATERIALDROPIN.action[.subfolder1[.subfolder2]...].ACTION</td>\r\n<td>shared.action.open-url<br>shared.action.company.freeze</td>\r\n<td>/dropins/setNgxMaterial/SETNGXMATERIALDROPIN/ts/actions[/subfolder1[/subfolder2]...]/ACTION.ts</td>\r\n</tr>\r\n<tr>\r\n<td>DROPIN[.subfolder1[.subfolder2]...]ACTION</td>\r\n<td>main.reload<br>main.mail.send-mail<br>main.company.juice.greeting</td>\r\n<td>/dropins/DROPIN/ts/actions[/subfolder1[/subfolder2]...]/ACTION.ts</td>\r\n</tr>\r\n<tr>\r\n<td>DROPIN.CHILDDROPIN[.subfolder1[.subfolder2]...].ACTION<br><em>(DROPIN name starts with \'set\')</em></td>\r\n<td>setAdmin.hr.mail.send<br>setAdmin.hr.mail.2030.send<br>setNgxMaterial.shared.ts.actions.set-value</td>\r\n<td>/dropins/DROPIN/CHILDDROPIN/ts/actions[/subfolder1[/subfolder2]...]/ACTION.ts</td>\r\n</tr>\r\n</tbody>\r\n</table>', NULL, 1, 'dib', NULL, NULL, 'doc283-dib'),
