    (22, NULL, NULL, NULL, NULL, '*ngIf="CONDITION"', 'Include this component in the DOM only when TypeScript CONDITION evaluates to TRUE. Use in conjunction with row.xxx (grid columns only), getValue() and getEnv() etc, eg *ngIf="getValue(\'age\')<5 && getEnv(\'role\'===\'mom\')"', 'dcc22-dib'),
    (23, NULL, NULL, NULL, NULL, '[ngStyle]="EXPRESSION"', 'Set the CSS of this component according to the TypeScript EXPRESSION which normally contains conditions. Eg. [ngStyle]="{\'padding\': \'6px 10px\', \'background-color\': (getValue(\'name\') < \'D\' ? \'red\' : (getValue(\'name\') < \'M\' ? \'yellow\' : \'blue\'))}"', 'dcc23-dib'),
    (24, NULL, NULL, NULL, NULL, '[hidden]="CONDITION"', 'Hide this component (but keep in DOM) when TypeScript CONDITION evaluates to TRUE. Eg. [hidden]="getValue(\'selectEnum\') > \'B\' || (getValue(\'name\', \'dibexTestClientForm\') ?? \'\').includes(\'Cheese\')"', 'dcc24-dib'),
    (25, NULL, NULL, NULL, NULL, '[dibDisabled]="CONDITION"', 'Disable this component when TypeScript CONDITION evaluates to TRUE. Eg. [dibDisabled]="!getValue(\'id\') || [\'A\', \'B\', \'C\'].includes(getValue(\'status\'))"', 'dcc25-dib'),
    (26, NULL, NULL, NULL, NULL, '[ngClass]="EXPRESSION"', 'Set the CSS Class of this component according to the TypeScript EXPRESSION which normally contains conditions. Eg. [ngClass]="!!row.admin_id ? \'adminValid\' : \'adminInvalid\'"', 'dcc26-dib'),
    (27, NULL, NULL, NULL, NULL, '[ngValue]="EXPRESSION"', '???', 'dcc27-dib'),
    (28, NULL, NULL, NULL, NULL, '[innerHtml]="EXPRESSION"', 'Set the inner HTML of this component (eg a span) according to the TypeScript EXPRESSION. Ensure the HTML is safe (does not originate from users unless santitised). Useful to include HTML contained in a field, eg. [innerHtml]="getValue(\'inputTitle\') | safeHtml".', 'dcc28-dib'),
    (29, NULL, NULL, 17, NULL, '[actionData]="row"', 'Used to provide a reference to the grid row object, in order to access field values in the row in for example the expression field or item event urls.', 'dcc29-dib'),
    (30, NULL, 'Buttons', NULL, NULL, 'mat-raised-button', 'Apply the Angular Material styling for raised buttons.', 'dcc30-dib'),
    (31, NULL, 'Buttons', NULL, NULL, 'mat-flat-button', 'Apply the Angular Material styling for flat buttons', 'dcc31-dib'),
    (32, NULL, 'Buttons', NULL, NULL, 'mat-icon-button', 'Apply the Angular Material styling for icon buttons', 'dcc32-dib'),
    (33, NULL, 'Buttons', NULL, NULL, 'mat-fab', 'Apply the Angular Material styling for fab buttons', 'dcc33-dib'),
    (34, NULL, 'Buttons', NULL, NULL, 'mat-mini-fab', 'Apply the Angular Material styling for mini-fab buttons', 'dcc34-dib'),
    (35, NULL, NULL, NULL, NULL, 'color="accent"', 'One of primary|accent|warn. Sets the background color of buttons and other Angular "mat-" components, like tabs. Guide: use accent for "special" action buttons that should stand out, and warn for buttons that destroy data, or fields that convey error states.', 'dcc35-dib'),
    (36, NULL, 'Data Components', NULL, NULL, 'appearance="outline"', 'One of standard|outline|fill (default is standard). Sets the appearance of form fields.', 'dcc36-dib'),
    (37, 66, NULL, NULL, NULL, 'cdkTextareaAutosize', 'Directive to automatically resize a textarea to fit its content', 'dcc37-dib'),
    (38, 66, NULL, NULL, NULL, 'cdkAutosizeMinRows="5"', 'Minimum amount of rows in the textarea', 'dcc38-dib'),
    (39, 66, NULL, NULL, NULL, 'cdkAutosizeMaxRows="10"', 'Maximum amount of rows in the textarea', 'dcc39-dib'),
    (40, 75, NULL, NULL, NULL, 'mode="determinate"', 'Whether progressbar grows incrementally as value is set (determinate) or spins in indeterminate fashion (indeterminate), or a secondary buffer bar also grows as bufferValue is set (buffer).', 'dcc40-dib'),
    (41, 30, NULL, NULL, NULL, 'startView="year"', 'The view that will show up when the calendar first opens. Valid values: month|year|multi-year. The default is month.', 'dcc41-dib'),
    (42, 98, NULL, NULL, NULL, 'unit="pixel"', 'Whether child area sizes are set using pixels (pixel) or percentages (percent). The default is percent. Note, if percent, all areas should add up to 100.', 'dcc42-dib'),
    (43, 99, NULL, NULL, NULL, 'unit="pixel"', 'Whether child area sizes are set using pixels (pixel) or percentages (percent). The default is percent. Note, if percent, all areas should add up to 100.', 'dcc43-dib'),
    (44, 97, NULL, NULL, NULL, '[size]="200"', 'Size of the area in selected unit (percent/pixel). Note, see rules about sizes in documentation to avoid issues. ', 'dcc44-dib'),
    (45, 97, NULL, NULL, NULL, '[maxSize]="200"', 'Maximum pixel or percent size, should be equal to or larger than provided [size]. Not working when [size]="\'*\'"', 'dcc45-dib'),
    (46, 97, NULL, NULL, NULL, '[minSize]="200"', 'Minimum pixel or percent size, should be equal to or smaller than provided [size]. Not working when [size]="\'*\'"', 'dcc46-dib'),
    (47, 97, NULL, NULL, NULL, '[lockSize]="true"', 'Lock area size. Not working when [size]="\'*\'"', 'dcc47-dib'),
    (48, 102, NULL, NULL, NULL, 'mat-stretch-tabs="false"', 'Do not stretch tabs to fill available area.', 'dcc48-dib'),
    (49, NULL, 'Layout', NULL, NULL, 'fill-height', 'Special DIB directive to set the CSS height property to fill the remaining height of the screen. Included as defaults in attributes3 on iframes and grids. \r\n<br>Extra attributes can be set:\r\n<br>[additionalSpace]=300 - leave 300px open at the bottom\r\n<br>[debounceTime]=300 - wait 300 milliseconds before calculating the height to give rendering engine time\r\n<br>[setProperty]="min-height" - set min-height instead of height\r\n<br>[debug]="true" - display debug info in the Console\r\n<br>[forcedSpace]=300 - max-height:300px will be set. [additionalSpace] and [footerElement] inputs are ignored.', 'dcc49-dib'),
    (50, NULL, 'Buttons', NULL, NULL, 'mat-stroked-button', 'Apply the Angular Material styling for stroked buttons', 'dcc50-dib'),
    (51, NULL, 'Buttons', NULL, NULL, 'mat-button', 'Apply the Angular Material styling for a normal buttons', 'dcc51-dib'),
    (52, NULL, 'Data Components', NULL, NULL, 'subscriptSizing="dynamic"', 'fixed | dynamic. Whether the form field should reserve space for one line of hint/error text (default) or to have the spacing grow from 0px as needed based on the size of the hint/error content. Note that when using dynamic sizing, layout shifts will occur when hint/error text changes.', 'dcc36-dib');
INSERT INTO `pef_docs_dropin` (`id`, `pef_dropin_id`, `container_setting`, `info`, `dibuid`) VALUES
    (1, 17, 'name=>footer;value=>false', 'show grid footer which contains page navigation and page size selector (default is true)', 'dcd1-dib'),
    (2, 17, 'name=>showSearch;value=>true', 'show the filter boxes at the top when the grid loads (default is false)', 'dcd2-dib'),
    (3, 17, 'name=>pageDefault;value=>20', 'set grid page size (default=20). Valid values: 10,20,50,100\r\n<ul>\r\n    <li>Set global default (for all grids) in pef_setting.</li>\r\n    <li>Tip - for grids that will never have many rows: set pageDefault and hide the footer</li>\r\n</ul>', 'dcd3-dib'),
    (4, 18, 'name=>doNotReloadiFrame;value=>true', 'do not automatically refresh iframe contents', 'dcd4-dib'),
    (5, 18, 'name=>GlobalsReplaceList;value=>REGEXEXPR', 'regular expression defining acceptable characters in parameter values, eg #[^a-zA-Z0-9_ ]*#. Used with preg_replace before parameters are merged with Eleutheria. The example is a whitelist (far more secure), but a blacklist could also be defined, eg #[|~]#', 'dcd5-dib'),
    (6, 18, 'name=>HtmlPurifier;value=>true', 'whether HtmlPurifier must be used to strip any unsafe code (default is false). HtmlPurifier adds quite a performance overhead, and is not needed if parameters are used safely, since Eleutheria HTML encodes parameter values by default.', 'dcd6-dib'),
    (7, 16, 'name=>validationTicks;value=>false', 'whether form components should contain validation ticks indicating the state of each field (default is true)', 'dcd7-dib'),
    (8, 17, 'name=>validationTicks;value=>false', 'whether form components inside an editable grid should contain validation ticks indicating the state of each field (default is true)', 'dcd8-dib'),
    (9, 17, 'name=>tableClass;value=>CLASSNAME', 'name of CSS class to apply to the HTML &lt;table&gt; tag of the grid. See Item Settings (hdrAttr, cellAttr) for styling of individual headers and columns in grids.', 'dcd9-dib'),
    (10, 17, 'name=>gridExcelEditingEnabled;value=>true', 'whether editing is automatically enabled on a editable grid (based on a table) (default is false). Alternatively, btnGridInlineEdit is required (outside columns). Set global setting for all containers in pef_setting, per module dropin.', 'dcd10-dib'),
    (11, 17, 'name=>gridExcelEditingAutoSave;value=>true', 'whether fields are automatically saved on lost of focus on a editable grid (based on a table) (default is false). Alternatively, btnGridInlineSave is required (inside columns). Set global setting for all containers in pef_setting, per module dropin.', 'dcd11-dib'),
    (12, 17, 'name=>gridMultiselect;value=>true', 'enable multi-select of rows (default is false - can only select one row). Set global setting for all containers in pef_setting, per module dropin.', 'dcd12-dib'),
