INSERT INTO `pef_script` (`id`, `name`, `category`, `subcategory`, `order_no`, `has_feature_script`, `apply_feature_script`, `remove_feature_script`, `short_help_html`, `long_help_html`, `after_apply_html`, `container_name`, `notes`, `dibuid`) VALUES
    (1, 'Side Menu', 'Base Container', NULL, 10, '/* \r\nPossible has_feature_script returns values (single column named \'result\')\r\n   no record or empty - the feature does not exist and can be applied\r\n   integer - the feature exists and won\'t be applied again, but can be removed\r\n   \'yes_run\' - the feature exists. If user selects \'Apply\' the script will be run again. If user selects \'Remove\', the feature is removed\r\n   \'any other string\' - the feature does not exist and can be applied. The string is displayed as part of help.\r\n*/\r\n\r\nSELECT i.id as result\r\nFROM pef_item i \r\n   INNER JOIN pef_component cp ON i.pef_component_id = cp.id\r\nWHERE\r\n   i.pef_container_id = :containerId AND cp.name = \'sidenavContainer\';', '/* First see if there is a projectHeader and get it\'s order_no */\r\n\r\nSELECT order_no as headerOrderNo\r\nFROM pef_item\r\nWHERE name = \'projectHeader\' AND pef_container_id = :containerId;\r\n\r\nSELECT CASE WHEN :headerOrderNo IS NULL THEN 10 ELSE :headerOrderNo + 10 END as headerOrderNo;\r\n\r\n/* Add sidenav components */\r\n\r\n-- sidenavContainerId  \r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, fx_direction, fx_align_cross, fx_flex_grow, fx_flex_shrink, fx_flex_base)\r\nSELECT \'sidenavContainer\', cp.id, c.id, :headerOrderNo, null, \'row\', \'stretch\', 1, 0, \'0%\'\r\nFROM pef_component cp \r\n     INNER JOIN pef_container c ON c.id = :containerId\r\nWHERE cp.name = \'sidenavContainer\';\r\n\r\n-- menuId\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, attributes)\r\nSELECT \'menu\', cp.id, c.id, 10, :sidenavContainerId, \'mode="side" closed\'\r\nFROM pef_component cp \r\n     INNER JOIN pef_container c ON c.id = :containerId\r\nWHERE cp.name = \'sidenav\';\r\n\r\n-- sidenavContentId\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, attributes)\r\nSELECT \'sidenavContent\', cp.id, c.id, 10, :sidenavContainerId, null\r\nFROM pef_component cp \r\n     INNER JOIN pef_container c ON c.id = :containerId\r\nWHERE cp.name = \'sidenavContent\';\r\n\r\n-- menuItemsId\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, attributes)\r\nSELECT \'menuItems\', cp.id, c.id, 10, :menuId, null\r\nFROM pef_component cp \r\n     INNER JOIN pef_container c ON c.id = :containerId\r\nWHERE cp.name = \'sidenavMenu\';\r\n\r\n/* Add logout */\r\n\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, expression, caption)\r\nSELECT \'mnLogout\', cp.id, c.id, 100, :menuItemsId, \'/logout\', \'Logout\'\r\nFROM pef_component cp \r\n     INNER JOIN pef_container c ON c.id = :containerId\r\nWHERE cp.name = \'sidenavMenuItem\';\r\n\r\n/* Whatever is already in the container, except the projectHeader, will be moved to the main body (sidenavContent) */\r\n\r\nUPDATE pef_item\r\nSET pef_item_id = :sidenavContentId\r\nWHERE pef_item_id IS NULL AND pef_container_id = :containerId AND name <> \'projectHeader\' AND id <> :sidenavContainerId;\r\n\r\n/* Add menu button */\r\n\r\nSELECT id as headerId\r\nFROM pef_item\r\nWHERE name = \'projectHeader\' AND pef_container_id = :containerId;\r\n\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, icon, attributes, fx_align_self)\r\nSELECT \'openNav\', cp.id, c.id, 5, :headerId, \'menu\', \'(click)="menu.toggle()"  mat-button\', \'flex-start\'\r\nFROM pef_component cp \r\n     INNER JOIN pef_container c ON c.id = :containerId\r\nWHERE cp.name = \'button\';', 'SELECT i.id as sidenavId\r\nFROM pef_item i \r\n    INNER JOIN pef_component cp ON i.pef_component_id = cp.id\r\nWHERE cp.name = \'sidenav\' AND i.pef_container_id = :containerId;\r\n\r\n/* Remove all descendants ... */\r\n\r\nDELETE i\r\nFROM pef_item i \r\n     INNER JOIN pef_item ii ON i.pef_item_id = ii.id\r\n     INNER JOIN pef_item iii ON ii.pef_item_id = iii.id\r\n     INNER JOIN pef_item iiii ON iii.pef_item_id = iiii.id\r\n     INNER JOIN pef_item iiiii ON iiii.pef_item_id = iiiii.id\r\nWHERE iiiii.id = :sidenavId AND i.pef_container_id = :containerId;\r\n\r\nDELETE i\r\nFROM pef_item i \r\n     INNER JOIN pef_item ii ON i.pef_item_id = ii.id\r\n     INNER JOIN pef_item iii ON ii.pef_item_id = iii.id\r\n     INNER JOIN pef_item iiii ON iii.pef_item_id = iiii.id\r\nWHERE iiii.id = :sidenavId AND i.pef_container_id = :containerId;\r\n\r\nDELETE i\r\nFROM pef_item i \r\n     INNER JOIN pef_item ii ON i.pef_item_id = ii.id\r\n     INNER JOIN pef_item iii ON ii.pef_item_id = iii.id\r\nWHERE iii.id = :sidenavId AND i.pef_container_id = :containerId;\r\n\r\nDELETE i\r\nFROM pef_item i \r\n     INNER JOIN pef_item ii ON i.pef_item_id = ii.id\r\nWHERE ii.id = :sidenavId AND i.pef_container_id = :containerId;\r\n\r\nDELETE i\r\nFROM pef_item i \r\nWHERE i.id = :sidenavId AND i.pef_container_id = :containerId;\r\n\r\n/* Move the contents in sidenavContent to root body */\r\n\r\nSELECT i.id as sidenavContentId\r\nFROM pef_item i \r\n     INNER JOIN pef_component cp ON i.pef_component_id = cp.id\r\nWHERE cp.name = \'sidenavContent\' AND i.pef_container_id = :containerId;\r\n\r\nUPDATE pef_item \r\nSET pef_item_id = null \r\nWHERE pef_item_id = :sidenavContentId;\r\n\r\n/* Delete the openNav button, sidenavContainer and sidenavContent */\r\n\r\nSELECT i.id as sidenavContainerId\r\nFROM pef_item i \r\n     INNER JOIN pef_component cp ON i.pef_component_id = cp.id\r\nWHERE cp.name = \'sidenavContainer\' AND i.pef_container_id = :containerId;\r\n\r\nDELETE i\r\nFROM pef_item i \r\nWHERE (i.name = \'openNav\' OR i.attributes LIKE \'%menu.toggle()%\') AND i.pef_container_id = :containerId;\r\n\r\nDELETE i\r\nFROM pef_item i \r\n    INNER JOIN pef_item ii ON i.pef_item_id = ii.id    \r\nWHERE ii.id = :sidenavContainerId AND i.pef_container_id = :containerId;\r\n\r\nDELETE i\r\nFROM pef_item i \r\nWHERE i.id = :sidenavContainerId AND i.pef_container_id = :containerId;\r\n', 'Add/Remove a side-menu with a button to open it, and a logout menu-item inside it.', '<img src="/files/dropins/dibCustom/designImages/dibScript Side Menu.jpg">', NULL, NULL, NULL, 'scr1-dib'),
    (2, 'Side Menu Links to Grids', 'Base Container', NULL, 20, '/* \r\nPossible has_feature_script returns values (single column named \'result\')\r\n   no record or empty - the feature does not exist and can be applied\r\n   integer - the feature exists and won\'t be applied again, but can be removed\r\n   \'yes_run\' - the feature exists. If user selects \'Apply\' the script will be run again. If user selects \'Remove\', the feature is removed\r\n   \'any other string\' - the feature does not exist and can be applied. The string is displayed as part of help.\r\n*/\r\n\r\nSELECT \r\n   CASE \r\n     WHEN ii.id IS NOT NULL THEN \'yes_run\'\r\n     WHEN i.id IS NOT NULL THEN null\r\n     ELSE \'<b>NOTE: First add a Side Menu.</b>\' \r\n   END as result\r\n\r\nFROM pef_component cp \r\n   LEFT JOIN pef_item i ON i.pef_component_id = cp.id AND i.pef_container_id = :containerId\r\n   LEFT JOIN pef_item ii ON ii.pef_item_id = i.id AND ii.pef_port_id IS NOT NULL\r\nWHERE\r\n   cp.name = \'sidenavMenu\'\r\nORDER BY (ii.id IS NOT NULL) DESC\r\nLIMIT 1', '/* Ensure that :itemId (if selected) is a valid menu item */\r\n\r\nSELECT i.id as itemId\r\nFROM pef_item i\r\n     INNER JOIN pef_component cp ON i.pef_component_id = cp.id\r\nWHERE  i.id = :itemId\r\n     AND (cp.name = \'sidenavMenu\' OR cp.name = \'sidenavMenuItem\') \r\n     AND i.pef_container_id = :containerId;\r\n\r\n/* Get existing sidenavMenuId and highest order_no in it*/\r\n\r\nSELECT i.id as sidenavMenuId\r\nFROM pef_item i\r\n     INNER JOIN pef_component cp ON i.pef_component_id = cp.id\r\nWHERE cp.name = \'sidenavMenu\' AND i.pef_container_id = :containerId;\r\n\r\nSELECT CASE WHEN :itemId IS NULL THEN :sidenavMenuId ELSE :itemId END as sidenavMenuId;\r\n\r\nSELECT max(order_no) as nextOrderNo\r\nFROM pef_item\r\nWHERE pef_item_id = :sidenavMenuId AND pef_container_id = :containerId;\r\n\r\nSELECT CASE WHEN :nextOrderNo IS NULL THEN 10 ELSE :nextOrderNo + 10 END as nextOrderNo;\r\n\r\n/* Add sidenavMenuItems */\r\n\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, pef_port_id, caption)\r\nSELECT ^^CONCAT(\'mn\', cc.name)^^ as linkName, cp.id, c.id, :nextOrderNo, :sidenavMenuId, p.id, cc.caption\r\nFROM pef_component cp \r\n     INNER JOIN pef_container c ON c.id = :containerId\r\n     INNER JOIN pef_table t ON t.id IN (__tableIds__)\r\n     INNER JOIN pef_container cc ON cc.pef_table_id = t.id\r\n     INNER JOIN pef_dropin d ON cc.pef_display_dropin_id = d.id\r\n     INNER JOIN pef_port p ON p.pef_container_id = cc.id AND p.port_alias = \'main\'\r\n     LEFT JOIN pef_item i ON i.name = ^^CONCAT(\'mn\', cc.name)^^ and i.pef_container_id = :containerId\r\nWHERE cp.name = \'sidenavMenuItem\' AND d.name = \'grid\' AND i.id IS NULL;\r\n\r\n/* Move existing menu items to new parent if needed */\r\nUPDATE pef_item i\r\n     INNER JOIN pef_component cp ON i.pef_component_id = cp.id\r\n     INNER JOIN pef_container c ON i.pef_container_id = c.id \r\n     INNER JOIN pef_table t ON t.id IN (__tableIds__)\r\n     INNER JOIN pef_container cc ON cc.pef_table_id = t.id\r\n     INNER JOIN pef_port p ON p.pef_container_id = cc.id AND p.port_alias = \'main\'\r\nSET i.pef_item_id = :sidenavMenuId\r\nWHERE i.pef_port_id = p.id\r\n      AND c.id = :containerId \r\n      AND cp.name = \'sidenavMenuItem\';\r\n      \r\n\r\n\r\n     \r\n', '/* Ensure that :itemId (if selected) is a valid menu item */\r\n\r\nSELECT i.id as itemId\r\nFROM pef_item i\r\n     INNER JOIN pef_component cp ON i.pef_component_id = cp.id\r\nWHERE  i.id = :itemId\r\n     AND (cp.name = \'sidenavMenu\' OR cp.name = \'sidenavMenuItem\') \r\n     AND i.pef_container_id = :containerId;\r\n\r\n/* Get existing sidenavMenuId */\r\n\r\nSELECT i.id as sidenavMenuId\r\nFROM pef_item i\r\n     INNER JOIN pef_component cp ON i.pef_component_id = cp.id\r\nWHERE cp.name = \'sidenavMenu\' AND i.pef_container_id = :containerId;\r\n\r\nSELECT CASE WHEN :itemId IS NULL THEN :sidenavMenuId ELSE :itemId END as sidenavMenuId;\r\n\r\n/* Remove links pointing to grid containers ... */\r\n\r\nDELETE i\r\nFROM pef_item i \r\n     INNER JOIN pef_container c ON i.pef_container_id = c.id\r\n     INNER JOIN pef_item ii ON i.pef_item_id = ii.id\r\n     INNER JOIN pef_port p ON i.pef_port_id = p.id\r\n     INNER JOIN pef_container cc ON p.pef_container_id = cc.id\r\n     INNER JOIN pef_table t ON cc.pef_table_id = t.id AND t.id IN (__tableIds__) \r\n     INNER JOIN pef_dropin d ON cc.pef_display_dropin_id = d.id\r\nWHERE ii.id = :sidenavMenuId AND i.pef_container_id = :containerId AND d.name = \'grid\';\r\n', 'Add/Move/Remove links in side-menu pointing to grids based on <b>selected tables</b>.\r\n<br>Optionally <b>select an Item</b> as parent for menu items, else root menu item is used. \r\n<br>Only missing/new links are added, and existing items are moved if Item is selected.', '<img src="/files/dropins/dibCustom/designImages/dibScript Side Menu Links.jpg">', NULL, NULL, NULL, 'scr2-dib'),
    (3, 'Add Tab in Side Menu with Links', 'Base Container', NULL, 30, '/* \r\nPossible has_feature_script returns values (single column named \'result\')\r\n   no record or empty - the feature does not exist and can be applied\r\n   integer - the feature exists and won\'t be applied again, but can be removed\r\n   \'yes_run\' - the feature exists. If user selects \'Apply\' the script will be run again. If user selects \'Remove\', the feature is removed\r\n   \'any other string\' - the feature does not exist and can be applied. The string is displayed as part of help.\r\n*/\r\n\r\nSELECT \r\n   CASE \r\n     WHEN tabs.id IS NOT NULL THEN \'yes_run\'\r\n     WHEN i.id IS NOT NULL THEN null\r\n     ELSE \'<b>NOTE: First add a Side Menu.</b>\' \r\n   END as result\r\n\r\nFROM pef_component cp \r\n   LEFT JOIN pef_item i ON i.pef_component_id = cp.id AND i.pef_container_id = :containerId\r\n   LEFT JOIN pef_item ii ON i.pef_item_id = ii.id\r\n   LEFT JOIN pef_component tabs ON ii.pef_component_id = tabs.id AND tabs.name = \'tabs\'\r\nWHERE\r\n    cp.name = \'sidenavMenu\'\r\nORDER BY (tabs.id IS NOT NULL) DESC\r\nLIMIT 1', '/* Get parameter values */\r\nSELECT cpTabs.id as cpTabsId, cpSideNavMenu.id as cpSideNavMenuId, iTabs.id as tabsId, iSideNav.id as sideNavId, count(iSideNavMenu.id) as countSideNavMenu, max(iSideNavMenu.order_no) as maxSideNavMenuOrderNo\r\nFROM pef_item iSideNavMenu\r\n     INNER JOIN pef_component cpSideNavMenu ON cpSideNavMenu.name = \'sidenavMenu\' AND iSideNavMenu.pef_component_id = cpSideNavMenu.id\r\n     INNER JOIN pef_component cpTabs ON cpTabs.name = \'tabs\'\r\n     INNER JOIN pef_component cpSideNav ON cpSideNav.name = \'sidenav\'\r\n     \r\n     INNER JOIN pef_item iSideNav ON iSideNav.pef_container_id = :containerId AND iSideNav.pef_component_id = cpSideNav.id\r\n     LEFT JOIN pef_item iTabs ON iSideNavMenu.pef_item_id = iTabs.id AND iTabs.pef_component_id = cpTabs.id     \r\nWHERE iSideNavMenu.pef_container_id = :containerId\r\nGROUP BY iTabs.id, iSideNav.id;\r\n\r\nSELECT CASE WHEN :maxSideNavMenuOrderNo IS NULL THEN 10 ELSE :maxSideNavMenuOrderNo + 10 END as maxSideNavMenuOrderNo;\r\n\r\n/* Add tabs component if needed */\r\n-- addTabsId\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, attributes)\r\nSELECT \'tabsMenu\' as tabsName, :cpTabsId, i.pef_container_id, 10, :sideNavId, \'mat-stretch-tabs="true"\'\r\nFROM pef_item i  \r\nWHERE i.id = :sideNavId AND :tabsId IS NULL;\r\n\r\nSELECT CASE WHEN :tabsId IS NULL THEN :addTabsId ELSE :tabsId END as tabsId;\r\n\r\n/* Move existing sideNavMenu(s) to new tabs if needed */\r\nUPDATE pef_item i \r\n    INNER JOIN pef_component cp on i.pef_component_id = cp.id\r\nSET i.pef_item_id = :tabsId, caption = \'Main\'\r\nWHERE cp.name = \'sidenavMenu\' AND i.pef_container_id = :containerId AND :countSideNavMenu = 1;\r\n\r\n/* Add a new tab */\r\n-- addSideNavId\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, caption)\r\nSELECT ^^CONCAT(\'tab\', (:countSideNavMenu + 1))^^ as tabsName, :cpSideNavMenuId, i.pef_container_id, :maxSideNavMenuOrderNo, :tabsId, CASE WHEN :countSideNavMenu > 0 THEN \'Reports\' ELSE \'Main\' END as caption\r\nFROM pef_item i  \r\nWHERE i.id = :tabsId AND :countSideNavMenu > 0;\r\n\r\n/* Add sidenavMenuItems */\r\n\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, pef_port_id, caption)\r\nSELECT ^^CONCAT(\'mn\', cc.name)^^ as linkName, cp.id, c.id, 10, :addSideNavId, p.id, cc.caption\r\nFROM pef_component cp \r\n     INNER JOIN pef_container c ON c.id = :containerId\r\n     INNER JOIN pef_table t ON t.id IN (__tableIds__)\r\n     INNER JOIN pef_container cc ON cc.pef_table_id = t.id\r\n     INNER JOIN pef_dropin d ON cc.pef_display_dropin_id = d.id\r\n     INNER JOIN pef_port p ON p.pef_container_id = cc.id AND p.port_alias = \'main\'\r\n     LEFT JOIN pef_item i ON i.name = ^^CONCAT(\'mn\', cc.name)^^ and i.pef_container_id = :containerId\r\nWHERE cp.name = \'sidenavMenuItem\' AND d.name = \'grid\' AND i.id IS NULL;\r\n\r\n/* Update existing links if they\'re in __tableIds__ */\r\n\r\nUPDATE pef_item i\r\n     INNER JOIN pef_component cp ON i.pef_component_id = cp.id \r\n     INNER JOIN pef_container c ON i.pef_container_id = c.id\r\n     INNER JOIN pef_table t ON t.id IN (__tableIds__)\r\n     INNER JOIN pef_container cc ON cc.pef_table_id = t.id\r\n /*    INNER JOIN pef_dropin d ON cc.pef_display_dropin_id = d.id AND d.name = \'grid\' */\r\n     INNER JOIN pef_port p ON p.pef_container_id = cc.id AND p.port_alias = \'main\'\r\nSET i.pef_item_id = :addSideNavId\r\nWHERE cp.name = \'sidenavMenuItem\' AND c.id = :containerId AND i.pef_port_id = p.id;\r\n\r\n', '/* Count how many tabs there are, and get the last one*/\r\n\r\nSELECT sideNav.id as sideNavId, i.id as tabsId, count(ii.id) as tabCount, max(ii.id) as maxSideNavMenuId\r\nFROM pef_item i \r\n     INNER JOIN pef_component cp ON i.pef_component_id = cp.id\r\n     INNER JOIN pef_item sideNav ON i.pef_item_id = sideNav.id\r\n     INNER JOIN pef_component cpSideNav ON sideNav.pef_component_id = cpSideNav.id\r\n     LEFT JOIN pef_item ii ON ii.pef_item_id = i.id\r\nWHERE cp.name = \'tabs\' AND cpSideNav.name = \'sidenav\' AND i.pef_container_id = :containerId\r\nGROUP BY sideNav.id,  i.id;\r\n\r\n/* Remove last tab added, and any of its child items */\r\n\r\nDELETE i\r\nFROM pef_item i \r\n    INNER JOIN pef_item ii ON i.pef_item_id = ii.id    \r\nWHERE ii.id = :maxSideNavMenuId AND i.pef_container_id = :containerId AND :tabCount > 1;\r\n\r\nDELETE i\r\nFROM pef_item i \r\nWHERE i.id = :maxSideNavMenuId AND i.pef_container_id = :containerId AND :tabCount > 1;\r\n\r\n/* If only one tab is left, move contents to sideNavMenu and delete tabs component */\r\nUPDATE pef_item i\r\nSET i.pef_item_id = :sideNavId\r\nWHERE i.id = :maxSideNavMenuId AND :tabCount = 1;\r\n\r\nDELETE i\r\nFROM pef_item i \r\nWHERE i.id = :tabsId AND :tabCount = 1;\r\n', 'Add/Remove a tab in the side-menu with links pointing to grids based on <b>selected tables</b>. \r\n<br>\'Remove\' removes only the last tab added (with any child items), and eventually the whole tab component.', '<img src="/files/dropins/dibCustom/designImages/dibScript Tab In Side Menu.jpg">', NULL, NULL, NULL, 'scr3-dib'),
    (4, 'Add Fixed Tabs to Main Body for Grids', 'Base Container', NULL, 40, '/* \r\nPossible has_feature_script returns values (single column named \'result\')\r\n   no record or empty - the feature does not exist and can be applied\r\n   integer - the feature exists and won\'t be applied again, but can be removed\r\n   \'yes_run\' - the feature exists. If user selects \'Apply\' the script will be run again. If user selects \'Remove\', the feature is removed\r\n   \'any other string\' - the feature does not exist and can be applied. The string is displayed as part of help.\r\n*/\r\n\r\nSELECT \r\n   CASE \r\n     WHEN i.id IS NOT NULL THEN \'yes_run\'\r\n     ELSE null\r\n   END as result\r\n\r\nFROM pef_component cp \r\n   LEFT JOIN pef_item i ON i.pef_component_id = cp.id AND i.pef_container_id = :containerId     \r\nWHERE\r\n   cp.name = \'tabs\' AND i.name IN (\'tabs\', \'mainHolder\')', '\r\n/* Get parameter values */\r\nSELECT cpTabs.id as cpTabsId, cpSideNavContent.id as cpSideNavContentId, iSideNavContent.id as sideNavContentId, iTabs.id as tabsId, \r\n       count(i.id) as countTabs, max(i.order_no) as maxTabOrderNo, min(i.order_no) as minTabOrderNo\r\nFROM pef_component cpTabs\r\n     INNER JOIN pef_component cpSideNavContent ON cpSideNavContent.name = \'sidenavContent\'\r\n     INNER JOIN pef_component cpSideNav ON cpSideNav.name = \'sidenav\'\r\n     LEFT JOIN pef_item iSideNavContent ON iSideNavContent.pef_component_id = cpSideNavContent.id AND iSideNavContent.pef_container_id = :containerId\r\n     LEFT JOIN pef_item iTabs ON iTabs.name IN (\'tabs\', \'mainHolder\') AND iTabs.pef_component_id = cpTabs.id AND iTabs.pef_container_id = :containerId\r\n     LEFT JOIN pef_item i ON (i.pef_item_id = iTabs.id AND i.name<>\'mainHolder\')\r\n     \r\nWHERE cpTabs.name = \'tabs\'\r\nGROUP BY cpTabs.id, cpSideNavContent.id, iSideNavContent.id, iTabs.id;\r\n\r\nSELECT CASE WHEN :sideNavContentId IS NULL THEN NULL ELSE :sideNavContentId END as mainBodyId;\r\n\r\n/* Add tabs component if needed */\r\n-- addTabsId\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, attributes)\r\nSELECT \'tabs\' as tabsName, id, :containerId, 15, :mainBodyId, \'mat-stretch-tabs="false"\'\r\nFROM pef_component   \r\nWHERE name=\'tabs\' AND :tabsId IS NULL;\r\n\r\nSELECT CASE WHEN :tabsId IS NULL THEN :addTabsId ELSE :tabsId END as tabsId;\r\n\r\n/* add tab for existing content if needed */\r\nSELECT CASE WHEN :maxTabOrderNo IS NULL THEN 10 ELSE :maxTabOrderNo + 10 END as maxTabOrderNo;\r\n\r\n-- newTabId\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, fx_direction, fx_align_main, fx_align_cross, fx_gap)\r\nSELECT \'tabMain\' as tabsName, id, :containerId, 5, :addTabsId, \'column\', \'flex-start\', \'stretch\', \'10px\'\r\nFROM pef_component   \r\nWHERE name=\'div\' AND :countTabs = 0;\r\n\r\n/* Move existing content in main body to new tab */\r\n\r\nUPDATE pef_item i \r\n    INNER JOIN pef_component cp on i.pef_component_id = cp.id\r\nSET i.pef_item_id = :newTabId\r\nWHERE (i.pef_item_id = :sideNavContentId OR (i.pef_item_id IS NULL AND :sideNavContentId IS NULL))\r\n    AND i.pef_container_id = :containerId \r\n    AND :countTabs = 0\r\n    AND i.id <> :addTabsId;\r\n    \r\n/* Create the new tabs, one for each table - skip where container is already linked to another port item on this container, or loaded in a subcontainer.\r\n  Note, if a container is linked to application\'s main Port Item (defined in pef_database), its port will be updated to link to the new tab instead (see UPDATE below).\r\n */\r\n\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, caption, fx_direction, fx_align_main, fx_align_cross, fx_gap)\r\nSELECT DISTINCT CONCAT(\'tab\', t.name) as tabName, cp.id, c.id, cc.id + :maxTabOrderNo as orderNo, :tabsId, cc.caption, \'column\', \'flex-start\', \'stretch\', \'10px\'\r\nFROM pef_component cp \r\n     INNER JOIN pef_container c ON c.id = :containerId\r\n     INNER JOIN pef_table t ON t.id IN (__tableIds__)\r\n     INNER JOIN pef_container cc ON cc.pef_table_id = t.id\r\n     INNER JOIN pef_dropin d ON cc.pef_display_dropin_id = d.id\r\n     INNER JOIN pef_database db ON t.pef_database_id = db.id\r\n     LEFT JOIN pef_port p ON p.pef_container_id = cc.id AND p.pef_item_id = db.pef_port_item_id\r\n     LEFT JOIN (\r\n        SELECT DISTINCT p.pef_container_id\r\n        FROM pef_port p\r\n            INNER JOIN pef_item i ON p.pef_item_id = i.id\r\n        WHERE i.pef_container_id = :containerId\r\n        UNION\r\n        SELECT DISTINCT pef_child_container_id\r\n        FROM pef_item\r\n        WHERE pef_child_container_id IS NOT NULL AND pef_container_id = :containerId AND pef_item_id = :tabsId\r\n    ) pc ON pc.pef_container_id = cc.id\r\nWHERE cp.name = \'div\' AND d.name = \'grid\' AND (pc.pef_container_id IS NULL OR p.id IS NOT NULL);\r\n\r\n/* UPDATE existing port records that point to application\'s main database Port Item (often named mainHolder) */\r\n\r\nUPDATE \r\npef_port p\r\n  INNER JOIN pef_container c ON p.pef_container_id = c.id\r\n  INNER JOIN pef_dropin d ON c.pef_display_dropin_id = d.id\r\n  INNER JOIN pef_table t ON c.pef_table_id = t.id AND t.id IN (__tableIds__)\r\n  INNER JOIN pef_database db ON t.pef_database_id = db.id\r\n  INNER JOIN pef_item i ON i.name = ^^CONCAT(\'tab\', t.name)^^\r\nSET p.pef_item_id = i.id\r\nWHERE p.port_alias = \'main\'\r\nAND (p.pef_item_id = db.pef_port_item_id OR p.pef_item_id IS NULL) \r\n   AND i.pef_container_id = :containerId;\r\n\r\n/* INSERT port records for other cases */ \r\n\r\nINSERT INTO pef_port (pef_container_id, pef_item_id, port_alias)\r\nSELECT DISTINCT c.id, i.id, c.name\r\nFROM pef_container c\r\n    INNER JOIN pef_table t ON c.pef_table_id = t.id AND t.id IN (__tableIds__)\r\n    INNER JOIN pef_dropin d ON c.pef_display_dropin_id = d.id\r\n    INNER JOIN pef_item i ON i.name = CONCAT(\'tab\', t.name)\r\n    LEFT JOIN (\r\n        SELECT DISTINCT p.pef_container_id\r\n        FROM pef_port p\r\n            INNER JOIN pef_item i ON p.pef_item_id = i.id\r\n        WHERE i.pef_container_id = :containerId\r\n        UNION\r\n        SELECT DISTINCT pef_child_container_id\r\n        FROM pef_item\r\n        WHERE pef_child_container_id IS NOT NULL AND pef_container_id = :containerId AND pef_item_id = :tabsId\r\n    ) pc ON pc.pef_container_id = c.id\r\nWHERE d.name = \'grid\' \r\n   AND pc.pef_container_id IS NULL \r\n   AND i.pef_container_id = :containerId;\r\n\r\n/* Update tab\'s pef_port_id fields to point to port records\r\n   Do it first for forms and then for grids so that grids takes priority if both exist. */\r\n   \r\nUPDATE pef_item i\r\nINNER JOIN pef_port p ON p.pef_item_id = i.id \r\nINNER JOIN pef_container c ON p.pef_container_id = c.id\r\nINNER JOIN pef_table t ON c.pef_table_id = t.id AND t.id IN (__tableIds__)\r\nINNER JOIN pef_dropin d ON c.pef_display_dropin_id = d.id AND d.name = \'form\'\r\nSET i.pef_port_id = p.id\r\nWHERE i.name = ^^CONCAT(\'tab\', t.name)^^ \r\n   AND i.pef_container_id = :containerId \r\n   AND i.pef_port_id IS NULL \r\n   AND i.caption = c.caption;\r\n   \r\nUPDATE pef_item i\r\nINNER JOIN pef_port p ON p.pef_item_id = i.id \r\nINNER JOIN pef_container c ON p.pef_container_id = c.id\r\nINNER JOIN pef_table t ON c.pef_table_id = t.id AND t.id IN (__tableIds__)\r\nINNER JOIN pef_dropin d ON c.pef_display_dropin_id = d.id AND d.name = \'grid\'\r\nSET i.pef_port_id = p.id\r\nWHERE i.name = ^^CONCAT(\'tab\', t.name)^^ \r\n   AND i.pef_container_id = :containerId \r\n   AND i.caption = c.caption;\r\n   \r\n/* See if the first tab is already linked to a subcontainer - if not, link the first container we\'re adding as a subcontainer and a port\r\n   (If a port is used for the first tab, it will not show the grid unless the user clicks on the tab since ports are activated on demand) */\r\n\r\nSELECT i.id as firstTabId, icp.name as firstTabCp, i.pef_child_container_id as firstTabChildC, i.pef_port_id as firstTabPortId\r\nFROM pef_item i\r\n     LEFT JOIN pef_component icp ON i.pef_component_id = icp.id\r\n     LEFT JOIN pef_item ii ON i.pef_item_id = ii.id\r\n     LEFT JOIN pef_component iicp ON ii.pef_component_id = iicp.id\r\nWHERE i.pef_container_id = :containerId \r\nAND iicp.name = \'tabs\'\r\nORDER BY i.order_no, i.id\r\nLIMIT 1;\r\n\r\nUPDATE pef_item i\r\n  INNER JOIN pef_component cpSubC ON cpSubC.name = \'subcontainer\'\r\n  INNER JOIN pef_port p ON p.id = :firstTabPortId\r\n  INNER JOIN pef_container c ON p.pef_container_id = c.id\r\n  INNER JOIN pef_table t ON c.pef_table_id = t.id AND t.id IN (__tableIds__) \r\n  SET i.pef_child_container_id = c.id, i.pef_component_id = cpSubC.id\r\nWHERE i.id = :firstTabId\r\n', '/* First mark items to be deleted */\r\nUPDATE \r\npef_port p\r\n  INNER JOIN pef_container c ON p.pef_container_id = c.id\r\n  INNER JOIN pef_table t ON c.pef_table_id = t.id AND t.id IN (__tableIds__)\r\n  INNER JOIN pef_dropin d ON c.pef_display_dropin_id = d.id\r\n  INNER JOIN pef_item i ON p.pef_item_id = i.id\r\n  INNER JOIN pef_item ii ON i.pef_item_id = ii.id AND ii.name IN (\'tabs\', \'mainHolder\')\r\n  LEFT JOIN pef_database db ON t.pef_database_id = db.id\r\n  LEFT JOIN pef_item mainItem ON db.pef_port_item_id = mainItem.id AND mainItem.pef_container_id = :containerId\r\n  \r\nSET i.pef_port_id = null, i.notes = \'__dib_delete_me__\'\r\nWHERE d.name = \'grid\' AND i.pef_container_id = :containerId AND (mainItem.id IS NULL OR mainItem.id <> i.id);\r\n\r\nUPDATE \r\npef_item i\r\n  INNER JOIN pef_container c ON i.pef_child_container_id = c.id\r\n  INNER JOIN pef_table t ON c.pef_table_id = t.id AND t.id IN (__tableIds__)\r\n  INNER JOIN pef_dropin d ON c.pef_display_dropin_id = d.id\r\n   INNER JOIN pef_item ii ON i.pef_item_id = ii.id AND ii.name IN (\'tabs\', \'mainHolder\')\r\n  LEFT JOIN pef_database db ON t.pef_database_id = db.id\r\n  LEFT JOIN pef_item mainItem ON db.pef_port_item_id = mainItem.id AND mainItem.pef_container_id = :containerId\r\n  \r\nSET i.pef_child_container_id = null, i.notes = \'__dib_delete_me__\'\r\nWHERE d.name = \'grid\' AND i.pef_container_id = :containerId AND (mainItem.id IS NULL OR mainItem.id <> i.id);\r\n\r\n\r\n/* Update related ports, set Port Item to application\'s Main Port Item if it exists, else to null */\r\n\r\nUPDATE \r\npef_port p\r\n  INNER JOIN pef_container c ON p.pef_container_id = c.id\r\n  INNER JOIN pef_table t ON c.pef_table_id = t.id AND t.id IN (__tableIds__)\r\n  INNER JOIN pef_dropin d ON c.pef_display_dropin_id = d.id\r\n  INNER JOIN pef_item i ON p.pef_item_id = i.id\r\n  INNER JOIN pef_item ii ON i.pef_item_id = ii.id AND ii.name IN (\'tabs\', \'mainHolder\')\r\n  INNER JOIN pef_component cpTabs ON cpTabs.name = \'tabs\'\r\n  LEFT JOIN pef_database db ON t.pef_database_id = db.id\r\n  LEFT JOIN pef_item mainItem ON (\r\n       db.pef_port_item_id = mainItem.id \r\n       AND mainItem.pef_container_id = :containerId \r\n       AND mainItem.pef_component_id <> cpTabs.id\r\n  )\r\n  \r\nSET p.pef_item_id = mainItem.id\r\nWHERE i.pef_container_id = :containerId AND (mainItem.id IS NULL OR mainItem.id <> i.id);\r\n\r\n/* Delete the tabs */\r\n\r\nDELETE i\r\nFROM pef_item i \r\n    INNER JOIN pef_item ii ON i.pef_item_id = ii.id    \r\nWHERE ii.notes = \'__dib_delete_me__\' AND i.pef_container_id = :containerId;\r\n\r\nDELETE i\r\nFROM pef_item i \r\nWHERE i.notes = \'__dib_delete_me__\' AND i.pef_container_id = :containerId;\r\n', 'Add/Remove tabs in the main body with links pointing to grids based on <b>selected tables</b>. \r\n<br>Will move existing containers linked to main Port Item to their own tab, and add additional tabs for new containers.\r\n<br>Note, any main application Port Items (eg mainHolder) defined in the Database table will not be removed.', '<img src="/files/dropins/dibCustom/designImages/dibScript Tabs in Body.jpg">', NULL, NULL, NULL, 'scr4-dib'),
    (5, 'Project Header With Logo/User/etc', 'Base Container', NULL, 5, '/* \r\nPossible has_feature_script returns values (single column named \'result\')\r\n   no record or empty - the feature does not exist and can be applied\r\n   integer - the feature exists and won\'t be applied again, but can be removed\r\n   \'yes_run\' - the feature exists. If user selects \'Apply\' the script will be run again. If user selects \'Remove\', the feature is removed\r\n   \'any other string\' - the feature does not exist and can be applied. The string is displayed as part of help.\r\n*/\r\n\r\nSELECT CASE \r\n/*    WHEN \r\n        iprojectHeader.id IS NOT NULL \r\n        AND ilogo.id IS NOT NULL\r\n        AND ititle.id IS NOT NULL\r\n        AND iloggedInUserRow.id IS NOT NULL\r\n        AND iuserIcon.id IS NOT NULL\r\n        AND iuser.id IS NOT NULL\r\n        AND ilogout.id IS NOT NULL\r\n        AND ienvironment.id IS NOT NULL \r\n    THEN iprojectHeader.id\r\n*/\r\n    WHEN (iprojectHeader.id IS NOT NULL \r\n          OR ilogo.id IS NOT NULL \r\n          OR ititle.id IS NOT NULL)\r\n    THEN \'yes_run\'\r\n    ELSE null\r\n    END as result\r\n\r\nFROM pef_component cpLogo\r\n    LEFT JOIN pef_item iprojectHeader ON iprojectHeader.pef_container_id = :containerId AND iprojectHeader.name=\'projectHeader\'\r\n    LEFT JOIN pef_item ilogo ON ilogo.pef_item_id = iprojectHeader.id AND ilogo.name=\'logo\'\r\n    LEFT JOIN pef_item ititle ON ititle.pef_item_id = iprojectHeader.id AND ititle.name=\'title\'\r\n    LEFT JOIN pef_item iloggedInUserRow ON iloggedInUserRow.pef_item_id = iprojectHeader.id AND iloggedInUserRow.name=\'loggedInUserRow\'\r\n    LEFT JOIN pef_item iuserIcon ON iuserIcon.pef_item_id = iloggedInUserRow.id AND iuserIcon.name=\'userIcon\'\r\n    LEFT JOIN pef_item iuser ON iuser.pef_item_id = iloggedInUserRow.id AND iuser.name=\'user\'\r\n    LEFT JOIN pef_item ilogout ON ilogout.pef_item_id = iloggedInUserRow.id AND ilogout.name=\'logout\'\r\n    LEFT JOIN pef_item ienvironment ON ienvironment.pef_item_id = iloggedInUserRow.id AND ienvironment.name=\'environment\' \r\nWHERE cpLogo.name = \'img\'', '/* Define defaults in-case there are no stored options */\r\n\r\n{\r\n "logo" : 0,\r\n "logoUri" : "\\/files\\/dropins\\/dibAdmin\\/images\\/logos\\/logo_trs.png",\r\n "title" : 0,\r\n "titleText" : "My Application",\r\n "logout" : 0,\r\n "environment" : 0,\r\n "user" : 0,\r\n "setBackGroundColor" : 0,\r\n "backGroundColor" : "#0074D9"\r\n }\r\n \r\n /* Find existing elements */\r\n \r\nSELECT cpLogo.id as cpLogoId, iprojectHeader.id as projectHeaderId, ilogo.id as logoId, \r\n    ititle.id as titleId, iloggedInUserRow.id as loggedInUserRowId, iuserIcon.id as userIconId, \r\n    iuser.id as userId, ilogout.id as logoutId, ienvironment.id as environmentId, ispacer.id as spacerId\r\n\r\nFROM pef_component cpLogo\r\n    LEFT JOIN pef_item iprojectHeader ON iprojectHeader.pef_container_id = :containerId AND iprojectHeader.name=\'projectHeader\'\r\n    LEFT JOIN pef_item ilogo ON ilogo.pef_item_id = iprojectHeader.id AND ilogo.name=\'logo\'\r\n    LEFT JOIN pef_item ititle ON ititle.pef_item_id = iprojectHeader.id AND ititle.name=\'title\'\r\n    LEFT JOIN pef_item ispacer ON ispacer.pef_item_id = iprojectHeader.id AND ispacer.name=\'headerSpacer\'\r\n    LEFT JOIN pef_item iloggedInUserRow ON iloggedInUserRow.pef_item_id = iprojectHeader.id AND iloggedInUserRow.name=\'loggedInUserRow\'\r\n    LEFT JOIN pef_item iuserIcon ON iuserIcon.pef_item_id = iloggedInUserRow.id AND iuserIcon.name=\'userIcon\'\r\n    LEFT JOIN pef_item iuser ON iuser.pef_item_id = iloggedInUserRow.id AND iuser.name=\'user\'\r\n    LEFT JOIN pef_item ilogout ON ilogout.pef_item_id = iloggedInUserRow.id AND ilogout.name=\'logout\'\r\n    LEFT JOIN pef_item ienvironment ON ienvironment.pef_item_id = iloggedInUserRow.id AND ienvironment.name=\'environment\' \r\nWHERE cpLogo.name = \'img\';\r\n\r\nSELECT CASE WHEN :backGroundColor IS NULL OR :backGroundColor=\'\' THEN \'#85144B\' ELSE :backGroundColor END as backGroundColor;\r\n\r\n/* UPDATE existing elements */\r\n\r\nUPDATE pef_item \r\nSET css =  CONCAT(\'background-color: \', :backGroundColor)\r\nWHERE pef_container_id = :containerId AND name=\'projectHeader\' AND :setBackGroundColor=1;\r\n\r\nUPDATE pef_item \r\nSET expression = :titleText\r\nWHERE pef_container_id = :containerId AND name=\'title\' AND pef_item_id = :projectHeaderId AND :title=1;\r\n\r\nUPDATE pef_item \r\nSET icon = :logoUri\r\nWHERE pef_container_id = :containerId AND name=\'logo\' AND pef_item_id = :projectHeaderId AND :logo=1;\r\n\r\n/* Add new elements */\r\n\r\n-- newProjectHeaderId\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, caption, fx_direction, fx_align_main, fx_align_cross, fx_gap, padding, css)\r\nSELECT \'projectHeader\', cp.id, :containerId, 5, null, null, \'row\', \'flex-start\', \'center\', \'10px\', \'8px\', CONCAT(\'background-color: \', :backGroundColor)\r\nFROM pef_component cp \r\nWHERE cp.name = \'row\' AND :projectHeaderId IS NULL AND (:logo=1 OR :title=1 OR :user=1 OR :environment=1 OR :logout=1);\r\n\r\nSELECT CASE WHEN :projectHeaderId IS NULL THEN :newProjectHeaderId ELSE :projectHeaderId END as projectHeaderId;\r\n\r\n/* Move button to open sideMenu to projectHeader, if it exists */\r\nUPDATE pef_item \r\nSET pef_item_id = :projectHeaderId, order_no = 5\r\nWHERE pef_container_id = :containerId AND attributes LIKE \'%menu.toggle()%\';\r\n\r\n/* Insert logo etc. */\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, icon, css, height)\r\nSELECT \'logo\', cp.id, :containerId, 20, :projectHeaderId, :logoUri, \'\', \'58px\'\r\nFROM pef_component cp \r\nWHERE cp.name = \'img\' AND :logoId IS NULL AND :logo=1;\r\n\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, expression, css)\r\nSELECT \'title\', cp.id, :containerId, 30, :projectHeaderId, :titleText, \r\n\'margin-left: 30px;\r\n font-size: 45px;\r\n font-weight: 500;\r\n font-family: sans-serif;\'\r\nFROM pef_component cp \r\nWHERE cp.name = \'span\' AND :titleId IS NULL AND :title=1;\r\n\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, fx_flex_base, fx_flex_grow)\r\nSELECT \'headerSpacer\', cp.id, :containerId, 40, :projectHeaderId, \'0%\', 1\r\nFROM pef_component cp \r\nWHERE cp.name = \'span\' AND :spacerId IS NULL AND (:user=1 OR :environment=1 OR :logout=1);\r\n\r\n-- newLoggedInUserRowId\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, caption, fx_direction, fx_align_main, fx_align_cross, fx_align_self, padding, margin, fx_gap, css)\r\nSELECT \'loggedInUserRow\', cp.id, :containerId, 50, :projectHeaderId, null, \'row\', \'flex-start\', \'center\', \'stretch\', \'7px\', \'5px\', \'7px\',\r\n\'background-color: #ededed;\r\nborder-radius: 5px;\r\nbox-shadow: 0px 0px 13px 2px rgba(0, 0, 0, 0.43);\'\r\n\r\nFROM pef_component cp \r\nWHERE cp.name = \'row\' AND :loggedInUserRowId IS NULL AND (:user=1 OR :environment=1 OR :logout=1);\r\n\r\nSELECT CASE WHEN :loggedInUserRowId IS NULL THEN :newLoggedInUserRowId ELSE :loggedInUserRowId END as loggedInUserRowId;\r\n\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, attributes, padding, fx_direction, fx_align_main, fx_align_cross, fx_align_self, css, expression)\r\nSELECT \'environment\', cp.id, :containerId, 10, :loggedInUserRowId, \'*ngIf="getEnv(\\\'environment\\\') == \\\'development\\\'"\', \'0px 10px\', \'row\', \'center\', \'center\', \'stretch\',\r\n\'color: white; background-color:green; font-size: 12px; border-radius: 5px;\', \r\n\'ENVIRONMENT:<br> DEVELOPMENT\'\r\nFROM pef_component cp \r\nWHERE cp.name = \'span\' AND :environmentId IS NULL AND :environment=1;\r\n\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, icon, css)\r\nSELECT \'userIcon\', cp.id, :containerId, 20, :loggedInUserRowId, \'account_circle\', \'\'\r\nFROM pef_component cp \r\nWHERE cp.name = \'icon\' AND :userIconId IS NULL AND :user=1;\r\n\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, expression, css)\r\nSELECT \'user\', cp.id, :containerId, 30, :loggedInUserRowId, \r\n\'{{getEnv(\\\'user_fullname\\\')}}\', \r\n\'color:black;\r\nmin-width: 120px;\r\nmax-width: 250px;\r\nfont-size: 16px;\r\nfont-weight: 500;\'\r\n\r\nFROM pef_component cp \r\nWHERE cp.name = \'spanInterpolation\' AND :userId IS NULL AND :user=1;\r\n\r\nINSERT INTO pef_item (name, pef_component_id, pef_container_id, order_no, pef_item_id, attributes , expression, icon)\r\nSELECT \'logout\', cp.id, :containerId, 40, :loggedInUserRowId, \'mat-raised-button color="warn"\', \'/logout\', \'logout\'\r\nFROM pef_component cp \r\nWHERE cp.name = \'button\' AND :logoutId IS NULL AND :logout=1;\r\n', '/* Define defaults in-case there are no stored options */\r\n\r\n{\r\n "logoRem" : 1,\r\n "titleRem" : 1,\r\n "logoutRem" : 1,\r\n "environmentRem" : 1,\r\n "userRem" : 1\r\n }\r\n \r\n /* Find existing elements */\r\n \r\nSELECT iprojectHeader.id as projectHeaderId, ilogo.id as logoId, \r\n    ititle.id as titleId, iloggedInUserRow.id as loggedInUserRowId, iuserIcon.id as userIconId, \r\n    iuser.id as userId, ilogout.id as logoutId, ienvironment.id as environmentId,\r\n    iopenNav.id as openNavId, ispacer.id as spacerId\r\n\r\nFROM pef_component cpLogo\r\n    LEFT JOIN pef_item iprojectHeader ON iprojectHeader.pef_container_id = :containerId AND iprojectHeader.name=\'projectHeader\'\r\n    LEFT JOIN pef_item ilogo ON ilogo.pef_item_id = iprojectHeader.id AND ilogo.name=\'logo\'\r\n    LEFT JOIN pef_item ititle ON ititle.pef_item_id = iprojectHeader.id AND ititle.name=\'title\'\r\n    LEFT JOIN pef_item ispacer ON ispacer.pef_item_id = iprojectHeader.id AND ispacer.name=\'headerSpacer\'\r\n    LEFT JOIN pef_item iloggedInUserRow ON iloggedInUserRow.pef_item_id = iprojectHeader.id AND iloggedInUserRow.name=\'loggedInUserRow\'\r\n    LEFT JOIN pef_item iuserIcon ON iuserIcon.pef_item_id = iloggedInUserRow.id AND iuserIcon.name=\'userIcon\'\r\n    LEFT JOIN pef_item iuser ON iuser.pef_item_id = iloggedInUserRow.id AND iuser.name=\'user\'\r\n    LEFT JOIN pef_item ilogout ON ilogout.pef_item_id = iloggedInUserRow.id AND ilogout.name=\'logout\'\r\n    LEFT JOIN pef_item ienvironment ON ienvironment.pef_item_id = iloggedInUserRow.id AND ienvironment.name=\'environment\' \r\n    LEFT JOIN pef_item iopenNav ON iopenNav.pef_item_id = iprojectHeader.id AND iopenNav.attributes LIKE \'%menu.toggle()%\' \r\nWHERE cpLogo.name = \'img\';\r\n\r\nDELETE \r\nFROM pef_item \r\nWHERE id = :logoutId AND pef_container_id = :containerId AND :logoutRem = 1;\r\n\r\nDELETE \r\nFROM pef_item \r\nWHERE id = :userId AND pef_container_id = :containerId AND :userRem = 1;\r\n\r\nDELETE \r\nFROM pef_item \r\nWHERE id = :userIconId AND pef_container_id = :containerId AND :userRem = 1;\r\n\r\nDELETE \r\nFROM pef_item \r\nWHERE id = :environmentId AND pef_container_id = :containerId AND :environmentRem = 1;\r\n\r\nDELETE \r\nFROM pef_item \r\nWHERE id = :loggedInUserRowId AND pef_container_id = :containerId \r\n      AND :logoutRem = 1 AND :environmentRem = 1 AND :userRem = 1;\r\n\r\nDELETE \r\nFROM pef_item \r\nWHERE id = :spacerId AND pef_container_id = :containerId \r\n      AND :logoutRem = 1 AND :environmentRem = 1 AND :userRem = 1;\r\n      \r\nDELETE \r\nFROM pef_item \r\nWHERE id = :titleId AND pef_container_id = :containerId AND :titleRem = 1;\r\n\r\nDELETE \r\nFROM pef_item \r\nWHERE id = :logoId AND pef_container_id = :containerId AND :logoRem = 1;\r\n\r\nDELETE \r\nFROM pef_item \r\nWHERE id = :projectHeaderId AND pef_container_id = :containerId \r\n      AND :logoutRem = 1 AND :environmentRem = 1 AND :userRem = 1 AND :titleRem = 1 AND :logoRem = 1\r\n      AND :openNavId IS NULL;\r\n      ', 'Add/Update/Remove the main Project Header containing logo, title, logged-in user, environment and logout button.\r\n<br>Use Options for specific elements to be added/removed.\r\n<br>Use the Designer to finalize CSS styling etc.', '<img src="/files/dropins/dibCustom/designImages/dibScript Project Header.jpg">', NULL, 'dibOptionProjectHeader', NULL, 'scr5-dib'),
    (6, 'Use Square Buttons in Grids', 'Buttons', NULL, 60, '/* \r\nPossible has_feature_script returns values (single column named \'result\')\r\n   no record or empty - the feature does not exist and can be applied\r\n   integer - the feature exists and won\'t be applied again, but can be removed\r\n   \'yes_run\' - the feature exists. If user selects \'Apply\' the script will be run again. If user selects \'Remove\', the feature is removed\r\n   \'any other string\' - the feature does not exist and can be applied. The string is displayed as part of help.\r\n*/\r\n\r\nSELECT CASE \r\n    WHEN \r\n        i.id IS NOT NULL THEN \'yes_run\'\r\n    ELSE null\r\n    END as result\r\n\r\nFROM pef_item i\r\n       INNER JOIN pef_container c ON i.pef_container_id = c.id\r\n       INNER JOIN pef_dropin d ON c.pef_display_dropin_id = d.id\r\n       INNER JOIN pef_component cp ON i.pef_component_id = cp.id\r\n       INNER JOIN pef_table t ON c.pef_table_id IN (__tableIds__)\r\nWHERE d.name = \'grid\' AND cp.name LIKE \'btn%\' AND i.attributes LIKE \'%mat-raised-button%\'\r\nLIMIT 1;', '/* Update all grids */\r\n\r\nUPDATE pef_item i\r\n       INNER JOIN pef_container c ON i.pef_container_id = c.id\r\n       INNER JOIN pef_dropin d ON c.pef_display_dropin_id = d.id\r\n       INNER JOIN pef_component cp ON i.pef_component_id = cp.id\r\n       INNER JOIN pef_table t ON c.pef_table_id IN (__tableIds__)\r\nSET i.attributes = REPLACE(i.attributes, \'mat-mini-fab\', \'mat-raised-button\')\r\nWHERE d.name = \'grid\' AND cp.name like \'btn%\';', '/* Update all grids */\r\n\r\nUPDATE pef_item i\r\n       INNER JOIN pef_container c ON i.pef_container_id = c.id\r\n       INNER JOIN pef_dropin d ON c.pef_display_dropin_id = d.id\r\n       INNER JOIN pef_component cp ON i.pef_component_id = cp.id\r\n       INNER JOIN pef_table t ON c.pef_table_id IN (__tableIds__)\r\nSET i.attributes = REPLACE(i.attributes, \'mat-raised-button\', \'mat-mini-fab\')\r\nWHERE d.name = \'grid\' AND cp.name like \'btn%\';', '<b>Apply</b> - replace <i>round</i> buttons with <i>square</i> buttons in grids linked to <b>selected tables</b>.\r\n<br><b>Remove</b> - replace <i>square</i> buttons with <i>round</i> buttons in grids linked to <b>selected tables</b>.', '<img src="/files/dropins/dibCustom/designImages/dibScript Square Buttons Grid.jpg">', NULL, NULL, NULL, 'scr6-dib'),
    (7, 'Use Square Buttons in Forms', 'Buttons', NULL, 60, '/* \r\nPossible has_feature_script returns values (single column named \'result\')\r\n   no record or empty - the feature does not exist and can be applied\r\n   integer - the feature exists and won\'t be applied again, but can be removed\r\n   \'yes_run\' - the feature exists. If user selects \'Apply\' the script will be run again. If user selects \'Remove\', the feature is removed\r\n   \'any other string\' - the feature does not exist and can be applied. The string is displayed as part of help.\r\n*/\r\n\r\nSELECT CASE \r\n    WHEN \r\n        i.id IS NOT NULL THEN \'yes_run\'\r\n    ELSE null\r\n    END as result\r\n\r\nFROM pef_item i\r\n       INNER JOIN pef_container c ON i.pef_container_id = c.id\r\n       INNER JOIN pef_dropin d ON c.pef_display_dropin_id = d.id\r\n       INNER JOIN pef_component cp ON i.pef_component_id = cp.id\r\n       INNER JOIN pef_table t ON c.pef_table_id IN (__tableIds__)\r\nWHERE d.name = \'form\' AND cp.name LIKE \'btn%\' AND i.attributes LIKE \'%mat-raised-button%\'\r\nLIMIT 1;', '/* Update all grids */\r\n\r\nUPDATE pef_item i\r\n       INNER JOIN pef_container c ON i.pef_container_id = c.id\r\n       INNER JOIN pef_dropin d ON c.pef_display_dropin_id = d.id\r\n       INNER JOIN pef_component cp ON i.pef_component_id = cp.id\r\n       INNER JOIN pef_table t ON c.pef_table_id IN (__tableIds__)\r\nSET i.attributes = REPLACE(i.attributes, \'mat-mini-fab\', \'mat-raised-button\')\r\nWHERE d.name = \'form\' AND cp.name like \'btn%\';', '/* Update all grids */\r\n\r\nUPDATE pef_item i\r\n       INNER JOIN pef_container c ON i.pef_container_id = c.id\r\n       INNER JOIN pef_dropin d ON c.pef_display_dropin_id = d.id\r\n       INNER JOIN pef_component cp ON i.pef_component_id = cp.id\r\n       INNER JOIN pef_table t ON c.pef_table_id IN (__tableIds__)\r\nSET i.attributes = REPLACE(i.attributes, \'mat-raised-button\', \'mat-mini-fab\')\r\nWHERE d.name = \'form\' AND cp.name like \'btn%\';', '<b>Apply</b> - replace <i>round</i> buttons with <i>square</i> buttons in forms linked to <b>selected tables</b>.\r\n<br><b>Remove</b> - replace <i>square</i> buttons with <i>round</i> buttons in forms linked to <b>selected tables</b>.', '<img src="/files/dropins/dibCustom/designImages/dibScript Square Buttons Form.jpg">', NULL, NULL, NULL, 'scr7-dib');
